    public ExcluirClienteView() {
        initComponents();
        ClienteDao cd = new ClienteDao();
        
        //Carregar clientes na combo
        for (int i = 0; i < cd.carregaClientes().size(); i++) {
            comboCliente.addItem(String.valueOf(cd.carregaClientes().get(i)));
        }
    }

